mapper.select:
SELECT compliance_rule.*, IFNULL(compliance_scan_result.compliance_count, 0) AS compliance_count, IFNULL(compliance_scan_result.not_compliance_count,0) AS not_compliance_count, IFNULL(compliance_scan_result.`scan_status`,1) as scan_status FROM compliance_rule compliance_rule LEFT JOIN ( SELECT resource_id, SUM(compliance_count) AS compliance_count, SUM(not_compliance_count) AS not_compliance_count, SUM(resource_count) AS resource_count, IF(FIND_IN_SET(2,GROUP_CONCAT(`status`)),2,1) as `scan_status`, GROUP_CONCAT(cloud_account_id) as cloud_account_id FROM compliance_scan_result
cloud_account_id=#{cloudAccountId}
GROUP BY resource_id ) compliance_scan_result ON compliance_scan_result.resource_id = compliance_rule.id ${ew.customSqlSegment}

